            HttpSession session = request.getSession();
            Cart cart = (Cart) session.getAttribute("cart");

            if (cart == null) {
                cart = new Cart();
            }

            cart.add(cartItem);

            // 5. 将购物车对象存入 session
            session.setAttribute("cart", cart);

            // 6. 重定向到 `cart.jsp` 页面
